Scripts.OnShow:
if (DMinstrel.MyLevel > 1) then
	self:Enable();
	self.Text:SetText("Полёт");
else
	self:Disable();
	self.Text:SetText("Полёт (недоступно)");
end
PlaySound(624, "SFX");

Scripts.OnTextChanged:
if (tonumber(self:GetText()) ~= nil or self:GetText() == "") then
	self:SetTextColor(1, 1, 1, 1);
else
	self:SetTextColor(1, 0, 0, 1);
end

